sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var totalamount = 0;
    var remainamount = 0;
    if (current.u_parent_project != '') {
        var bill = new GlideRecord('u_monthly_bills');
        bill.addEncodedQuery('u_parent_project=' + current.u_parent_project);
        bill.query();
        while (bill.next()) {
            totalamount += parseFloat(bill.u_amount_due);
            remainamount += parseFloat(bill.u_amount_paid);
        }

        var gt = new GlideRecord('u_casita_monthly_project');
        gt.addEncodedQuery('u_active_project=true');
        gt.setWorkflow(false);
        gt.query();
        if (gt.next()) {
            gt.u_total_bills_amount_due = totalamount;
            gt.u_remaining_monthly_bill_amount = (totalamount - remainamount);
            gt.update();
        }
    } else {
        var sbill = new GlideRecord('u_monthly_bills');
        sbill.addEncodedQuery('u_parent_sub_project=' + current.u_parent_sub_project);
        sbill.query();
        while (sbill.next()) {
            totalamount += parseFloat(sbill.u_amount_due);
            remainamount += parseFloat(sbill.u_amount_paid);
        }

        var sgt = new GlideRecord('u_sub_casita_monthly_project');
        sgt.addEncodedQuery('sys_id=' + current.u_parent_sub_project);
        sgt.setWorkflow(false);
        sgt.query();
        if (sgt.next()) {
            sgt.u_total_bills_amount_due = totalamount;
            sgt.u_remaining_monthly_bill_amount = (totalamount - remainamount);
            sgt.update();
        }
    }

})(current, previous);
