sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var totalamount = 0;
    if (current.u_project != '') {
        var pay = new GlideRecord('u_paycheck');
        pay.addEncodedQuery('u_project=' + current.u_project);
        pay.query();
        while (pay.next()) {
            totalamount += parseFloat(pay.u_paycheck_amount);
        }

        var gt = new GlideRecord('u_casita_monthly_project');
        gt.addEncodedQuery('u_active_project=true');
        gt.setWorkflow(false);
        gt.query();
        if (gt.next()) {
            gt.u_monthly_income = totalamount;
            gt.update();
        }
    } else {
        var spay = new GlideRecord('u_paycheck');
        spay.addEncodedQuery('u_sub_project=' + current.u_sub_project);
        spay.query();
        while (spay.next()) {
            totalamount += parseFloat(spay.u_paycheck_amount);
        }

        var sgt = new GlideRecord('u_sub_casita_monthly_project');
        sgt.addEncodedQuery('sys_id=' + current.u_parent_sub_project);
        sgt.setWorkflow(false);
        sgt.query();
        if (sgt.next()) {
            sgt.u_monthly_income = totalamount;            
            sgt.update();
        }
    }

})(current, previous);
