sys_script_client.script:
function onSubmit() {
    // Check for a specific condition (e.g., if the 'priority' field is '1' - Critical)
    if (g_form.getActionName() === 'start_project') {
        var userResponse = confirm("Starting this sub-project will deactivate the existing sub-project. Do you want to proceed?");

        // If the user clicks 'Cancel' (userResponse is false), abort the submission
        if (userResponse == false) {
            // Optional: prevent the 'dirty form' message from appearing when navigating away
            g_form.submitted = false;
            return false; // Aborts the form submission
        }
    }
    // If the condition is not met, or the user clicked 'OK', allow submission to continue
    return true; // Allows the form submission to continue
}
