sys_script_client.script:
function onLoad() {

    if (g_form.getValue('u_parent_project')) {
        var ga = new GlideAjax('global.CasitaUtils');
        ga.addParam('sysparm_name', 'grabAvailableFunds');
        ga.addParam('sysparm_parent', g_form.getValue('u_parent_project'));
		ga.addParam('sysparm_sub_parent', '');
        ga.getXMLAnswer(setFunds);
    } else{
		var ga = new GlideAjax('global.CasitaUtils');
        ga.addParam('sysparm_name', 'grabAvailableFunds');
		ga.addParam('sysparm_parent', '');
        ga.addParam('sysparm_sub_parent', g_form.getValue('u_parent_sub_project'));
        ga.getXMLAnswer(setFunds);
	}
}

function setFunds(answer) {
    g_form.setValue('u_available_funds', answer);
}
